(1931, '_wpallexport_session_1_', 'a:7:{s:14:\"is_user_export\";b:0;s:17:\"is_comment_export\";b:0;s:18:\"is_taxonomy_export\";b:0;s:3:\"cpt\";s:21:\"a:1:{i:0;s:4:\"post\";}\";s:11:\"whereclause\";s:0:\"\";s:10:\"joinclause\";s:6:\"a:0:{}\";s:11:\"exportquery\";s:72466:\"O:8:\"WP_Query\":53:{s:5:\"query\";a:5:{s:9:\"post_type\";a:1:{i:0;s:4:\"post\";}s:11:\"post_status\";s:3:\"any\";s:7:\"orderby\";s:2:\"ID\";s:5:\"order\";s:3:\"ASC\";s:14:\"posts_per_page\";i:10;}s:10:\"query_vars\";a:67:{s:9:\"post_type\";a:1:{i:0;s:4:\"post\";}s:11:\"post_status\";s:3:\"any\";s:7:\"orderby\";s:2:\"ID\";s:5:\"order\";s:3:\"ASC\";s:14:\"posts_per_page\";i:10;s:5:\"error\";s:0:\"\";s:1:\"m\";s:0:\"\";s:1:\"p\";i:0;s:11:\"post_parent\";s:0:\"\";s:7:\"subpost\";s:0:\"\";s:10:\"subpost_id\";s:0:\"\";s:10:\"attachment\";s:0:\"\";s:13:\"attachment_id\";i:0;s:4:\"name\";s:0:\"\";s:8:\"pagename\";s:0:\"\";s:7:\"page_id\";i:0;s:6:\"second\";s:0:\"\";s:6:\"minute\";s:0:\"\";s:4:\"hour\";s:0:\"\";s:3:\"day\";i:0;s:8:\"monthnum\";i:0;s:4:\"year\";i:0;s:1:\"w\";i:0;s:13:\"category_name\";s:0:\"\";s:3:\"tag\";s:0:\"\";s:3:\"cat\";s:0:\"\";s:6:\"tag_id\";s:0:\"\";s:6:\"author\";s:0:\"\";s:11:\"author_name\";s:0:\"\";s:4:\"feed\";s:0:\"\";s:2:\"tb\";s:0:\"\";s:5:\"paged\";i:0;s:8:\"meta_key\";s:0:\"\";s:10:\"meta_value\";s:0:\"\";s:7:\"preview\";s:0:\"\";s:1:\"s\";s:0:\"\";s:8:\"sentence\";s:0:\"\";s:5:\"title\";s:0:\"\";s:6:\"fields\";s:0:\"\";s:10:\"menu_order\";s:0:\"\";s:5:\"embed\";s:0:\"\";s:12:\"category__in\";a:0:{}s:16:\"category__not_in\";a:0:{}s:13:\"category__and\";a:0:{}s:8:\"post__in\";a:0:{}s:12:\"post__not_in\";a:0:{}s:13:\"post_name__in\";a:0:{}s:7:\"tag__in\";a:0:{}s:11:\"tag__not_in\";a:0:{}s:8:\"tag__and\";a:0:{}s:12:\"tag_slug__in\";a:0:{}s:13:\"tag_slug__and\";a:0:{}s:15:\"post_parent__in\";a:0:{}s:19:\"post_parent__not_in\";a:0:{}s:10:\"author__in\";a:0:{}s:14:\"author__not_in\";a:0:{}s:14:\"search_columns\";a:0:{}s:19:\"ignore_sticky_posts\";b:0;s:16:\"suppress_filters\";b:0;s:13:\"cache_results\";b:1;s:22:\"update_post_term_cache\";b:1;s:22:\"update_menu_item_cache\";b:0;s:19:\"lazy_load_term_meta\";b:1;s:22:\"update_post_meta_cache\";b:1;s:8:\"nopaging\";b:0;s:17:\"comments_per_page\";s:2:\"50\";s:13:\"no_found_rows\";b:0;}s:9:\"tax_query\";O:12:\"WP_Tax_Query\":6:{s:7:\"queries\";a:0:{}s:8:\"relation\";s:3:\"AND\";s:16:\"\0*\0table_aliases\";a:0:{}s:13:\"queried_terms\";a:0:{}s:13:\"primary_table\";s:8:\"wp_posts\";s:17:\"primary_id_column\";s:2:\"ID\";}s:10:\"meta_query\";O:13:\"WP_Meta_Query\":9:{s:7:\"queries\";a:0:{}s:8:\"relation\";N;s:10:\"meta_table\";N;s:14:\"meta_id_column\";N;s:13:\"primary_table\";N;s:17:\"primary_id_column\";N;s:16:\"\0*\0table_aliases\";a:0:{}s:10:\"\0*\0clauses\";a:0:{}s:18:\"\0*\0has_or_relation\";b:0;}s:10:\"date_query\";b:0;s:7:\"request\";s:465:\"SELECT SQL_CALC_FOUND_ROWS  wp_posts.*\n					 FROM wp_posts \n					 WHERE 1=1  AND wp_posts.post_type = \'post\' AND ((wp_posts.post_status <> \'trash\' AND wp_posts.post_status <> \'auto-draft\')) AND wp_posts.ID NOT IN (SELECT o.ID FROM wp_posts o\n                            LEFT OUTER JOIN wp_posts r ON o.post_parent = r.ID WHERE ((r.post_status = \'trash\' OR r.ID IS NULL) AND o.post_type = \'product_variation\'))\n					 \n					 ORDER BY wp_posts.ID ASC\n					 LIMIT 0, 10\";s:5:\"posts\";a:10:{i:0;O:7:\"WP_Post\":24:{s:2:\"ID\";i:1;s:11:\"post_author\";s:1:\"1\";s:9:\"post_date\";s:19:\"2024-07-17 21:10:03\";s:13:\"post_date_gmt\";s:19:\"2024-07-17 13:10:03\";s:12:\"post_content\";s:7799:\"<p><!-- wp:heading {\"level\":1} --></p><p><!-- /wp:heading --></p><p><!-- wp:heading --></p><h2 class=\"wp-block-heading\">简介</h2><p><!-- /wp:heading --></p><p><!-- wp:paragraph --></p><p><strong>思源派</strong>是一个专为<strong>思源笔记</strong>用户设计的内容分享平台。在这里，你可以找到丰富的资源，包括但不限于CSS片段、JavaScript脚本、内测插件以及高级使用教程，帮助你完全自由地定义和优化你的思源笔记体验。</p><p><!-- /wp:paragraph --></p><p><!-- wp:heading --></p><h2 class=\"wp-block-heading\">功能亮点</h2><p><!-- /wp:heading --></p><p><!-- wp:list --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><!-- wp:list-item --><li><strong>个性化定制</strong>：通过CSS和JS脚本，你可以按照自己的喜好和需求定制笔记的外观和功能。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><li><strong>内测插件</strong>：获取最新开发的插件，享受前沿功能。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><li><strong>高级教程</strong>：深入学习如何更高效地使用思源笔记，提升你的生产力。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- /wp:list --></p><p><!-- wp:heading --></p><h2 class=\"wp-block-heading\">资源分类</h2><p><!-- /wp:heading --></p><p><!-- wp:heading {\"level\":3} --></p><h3 class=\"wp-block-heading\">CSS 片段</h3><p><!-- /wp:heading --></p><p><!-- wp:list --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><!-- wp:list-item --><li><strong>主题样式</strong>：从经典到现代，多种主题风格供你选择。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><li><strong>布局调整</strong>：调整笔记的布局，使其更符合你的阅读和编辑习惯。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><li><strong>字体与颜色</strong>：自定义字体和颜色，提升阅读体验。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- /wp:list --></p><p><!-- wp:heading {\"level\":3} --></p><h3 class=\"wp-block-heading\">JavaScript 脚本</h3><p><!-- /wp:heading --></p><p><!-- wp:list --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><!-- wp:list-item --><li><strong>自动化操作</strong>：通过脚本实现笔记的自动化管理，如自动归档、标签管理等。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><li><strong>增强功能</strong>：扩展思源笔记的基本功能，如增加新的快捷键、搜索优化等。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- /wp:list --></p><p><!-- wp:heading {\"level\":3} --></p><h3 class=\"wp-block-heading\">内测插件</h3><p><!-- /wp:heading --></p><p><!-- wp:list --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><!-- wp:list-item --><li><strong>预览新功能</strong>：参与内测，体验即将发布的新功能。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><li><strong>反馈与优化</strong>：提供反馈，帮助开发者优化插件，提升用户体验。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- /wp:list --></p><p><!-- wp:heading {\"level\":3} --></p><h3 class=\"wp-block-heading\">高级使用教程</h3><p><!-- /wp:heading --></p><p><!-- wp:list --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><!-- wp:list-item --><li><strong>基础操作</strong>：从零开始，掌握思源笔记的基本使用方法。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><li><strong>高级技巧</strong>：学习高级技巧，如笔记间的链接、知识图谱的构建等。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><li><strong>案例分析</strong>：通过实际案例，了解如何将思源笔记应用到不同的工作和生活场景中。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- /wp:list --></p><p><!-- wp:heading --></p><h2 class=\"wp-block-heading\">社区互动</h2><p><!-- /wp:heading --></p><p><!-- wp:list --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><!-- wp:list-item --><li><strong>讨论区</strong>：加入我们的社区，与其他用户交流心得，分享你的创意和解决方案。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><li><strong>求助与支持</strong>：遇到问题时，可以在这里寻求帮助，或提供你的解决方案。</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- /wp:list --></p><p><!-- wp:heading --></p><h2 class=\"wp-block-heading\">如何开始</h2><p><!-- /wp:heading --></p><p><!-- wp:list {\"ordered\":true} --></p><ol class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ol class=\"wp-block-list\"><!-- wp:list-item --><li><strong>注册账号</strong>：访问 <a href=\"https://sypai.cc\">思源派</a> 并注册账号。</li></ol></li></ol><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ol class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ol class=\"wp-block-list\"><li><strong>浏览资源</strong>：在网站上浏览各类资源，找到你需要的内容。</li></ol></li></ol><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ol class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ol class=\"wp-block-list\"><li><strong>下载与应用</strong>：下载资源并应用到你的思源笔记中，开始你的个性化之旅。</li></ol></li></ol><p><!-- /wp:list-item --></p><p><!-- /wp:list --></p><p><!-- wp:heading --></p><h2 class=\"wp-block-heading\">联系方式</h2><p><!-- /wp:heading --></p><p><!-- wp:list --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><!-- wp:list-item --><li><strong>邮箱</strong>：3543118607@qq.com</li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- wp:list-item --></p><ul class=\"wp-block-list\"><li style=\"list-style-type: none;\"><ul class=\"wp-block-list\"><li><strong>社交媒体</strong>：QQ群链接：<a href=\"https://qm.qq.com/q/xjNArsK21a\">点击链接加入群聊【笔记软件综合交流群】</a></li></ul></li></ul><p><!-- /wp:list-item --></p><p><!-- /wp:list --></p><p><!-- wp:heading --></p><h2 class=\"wp-block-heading\">结语</h2><p><!-- /wp:heading --></p><p><!-- wp:paragraph --></p><p>思源派不仅仅是一个资源分享平台，更是一个创意和知识交流的社区。我们期待你的加入，一起探索数字生活的无限可能！</p><p><!-- /wp:paragraph --></p><p><!-- wp:separator --></p><hr class=\"wp-block-separator has-alpha-channel-opacity\" /><p><!-- /wp:separator --></p><p><!-- wp:paragraph --></p><p>如果你觉得我们的服务对你有帮助，可以推荐给更多人使用！</p><p><!-- /wp:paragraph --></p>\";s:10:\"post_title\";s:42:\"思源派：定义你自己的思源笔记\";s:12:\"post_excerpt\";s:0:\"\";s:11:\"post_status\";s:7:\"publish\";s:14:\"comment_status\";s:4:\"open\";s:11:\"ping_status\";s:4:\"open\";s:13:\"post_password\";s:0:\"\";s:9:\"post_name\";s:11:\"hello-world\";s:7:\"to_ping\";s:0:\"\";s:6:\"pinged\";s:0:\"\";s:13:\"post_modified\";s:19:\"2024-07-18 20:38:05\";s:17:\"post_modified_gmt\";s:19:\"2024-07-18 12:38:05\";s:21:\"post_content_filtered\";s:0:\"\";s:11:\"post_parent\";i:0;s:4:\"guid\";s:21:\"https://sypai.cc/?p=1\";s:10:\"menu_order\";i:0;s:9:\"post_type\";s:4:\"post\";s:14:\"post_mime_type\";s:0:\"\";s:13:\"comment_count\";s:1:\"2\";s:6:\"filter\";s:3:\"raw\";}i:1;O:7:\"WP_Post\":24:{s:2:\"ID\";i:45;s:11:\"post_author\";s:1:\"5\";s:9:\"post_date\";s:19:\"2024-07-18 11:30:25\";s:13:\"post_date_gmt\";s:19:\"2024-07-18 03:30:25\";s:12:\"post_content\";s:948:\"<p>思源插件开发当中，可以通过在 <code>plugin</code> 中注册 <code>protyleSlash</code>来自定义<code>/</code>命令。</p><p>这类  API 的时候一般是在 callback 回调函数中调用 <code>protyle.insert(&lt;content&gt;)</code> 在编辑器中插入我们想要的内容。</p><p>但是有些情况我们可能不想要插入内容，而是想要做别的操作，这就需要我们手动清理掉输入的 <code>/xxx</code>了。</p><p>具体方法是插入一个 <code>Lute.Carte</code> 字符，来清空前面的输入。</p><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"javascript\">protyle.insert(Lute.Carte);</code></pre><p>参考案例：<a href=\"https://github.com/frostime/sy-quick-attr/blob/c127d99762cee485ec597e29e479c6356f3177f7/src/index.ts#L255\">https://github.com/frostime/sy-quick-attr/blob/c127d99762cee485ec597e29e479c6356f3177f7/src/index.ts#L255</a></p>\";s:10:\"post_title\";s:69:\"如何在 / 命令的回调函数中手动删除前面输入的命令\";s:12:\"post_excerpt\";s:0:\"\";s:11:\"post_status\";s:7:\"publish\";s:14:\"comment_status\";s:4:\"open\";s:11:\"ping_status\";s:4:\"open\";s:13:\"post_password\";s:0:\"\";s:9:\"post_name\";s:190:\"%e5%a6%82%e4%bd%95%e5%9c%a8-%e5%91%bd%e4%bb%a4%e7%9a%84%e5%9b%9e%e8%b0%83%e5%87%bd%e6%95%b0%e4%b8%ad%e6%89%8b%e5%8a%a8%e5%88%a0%e9%99%a4%e5%89%8d%e9%9d%a2%e8%be%93%e5%85%a5%e7%9a%84%e5%91%bd\";s:7:\"to_ping\";s:0:\"\";s:6:\"pinged\";s:0:\"\";s:13:\"post_modified\";s:19:\"2024-07-18 12:13:05\";s:17:\"post_modified_gmt\";s:19:\"2024-07-18 04:13:05\";s:21:\"post_content_filtered\";s:0:\"\";s:11:\"post_parent\";i:0;s:4:\"guid\";s:22:\"https://sypai.cc/?p=45\";s:10:\"menu_order\";i:0;s:9:\"post_type\";s:4:\"post\";s:14:\"post_mime_type\";s:0:\"\";s:13:\"comment_count\";s:1:\"0\";s:6:\"filter\";s:3:\"raw\";}i:2;O:7:\"WP_Post\":24:{s:2:\"ID\";i:52;s:11:\"post_author\";s:1:\"9\";s:9:\"post_date\";s:19:\"2024-07-18 13:16:20\";s:13:\"post_date_gmt\";s:19:\"2024-07-18 05:16:20\";s:12:\"post_content\";s:11594:\"<div class=\"quote_q quote-mce qe_wzk_c-red\" contenteditable=\"false\"><div contenteditable=\"true\"><span style=\"color: #ff6600\">此文内容已过时，等待更新中。可先直达源码学习</span> <span style=\"color: #3366ff\"><a style=\"color: #3366ff\" href=\"https://github.com/prototype-validation/dist-repo-use-branch/tree/main/.github\">prototype-validation/dist-repo-use-branch</a></span></div></div><p>汐洛最近正在规划集中仓发布模式，其中通过分支发布的工作流是重点。</p><p>为了验证这一思路是否可行，汐洛通过 IntelliJ 插件 仓库进行原型验证测试。测试结果表明，这一思路基本可行。</p><p>当然，分支发布不是什么新鲜事，大佬们有更好的实现方式，这里仅以汐洛探索的成果进行分享。</p><p>接下来分为主仓库端（上游）和 fork 端（下游）展示其工作流。</p><h2>主仓库端工作流</h2><p>先附上完整的 action yaml 代码</p><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"yaml\"># 有个小缺陷需要解决，fokr端（下游）创建分支时必须同名，否则只有创建时是复制的制定分支，后续目标会被强制改为上游默认分支\nname: Test Push Directory to Branch with PR\n\n# 这里通过主分支的 push 触发，也可以自定义其他触发方式\non:\n  push:\n    branches:\n      - main\n\n# ref https://docs.github.com/zh/actions/learn-github-actions/variables\nenv:\n  dist_path: \".github\"\n  dist_branch: \"main_-_dist_-_test\" # 不能包含 / 等字符\n  main_branch: \"main\"\n\njobs:\n  build:\n    runs-on: ubuntu-latest\n    steps:\n      - name: Checkout repository\n        uses: actions/checkout@v4\n        with:\n          fetch-depth: 0  # 确保可以获取所有历史记录，以便合并\n          token: ${{ secrets.MY_GH_TOKEN_W }} # 使用具有完全写入权限的 TOKEN（必须包含 workflows ）\n\n      - name: Set up Git\n        run: |\n          git config --global user.name \'GitHub Actions\'\n          git config --global user.email \'actions@github.com\'\n\n      - name: Merge changes from main into dist_branch\n        run: |\n          git checkout ${{ env.main_branch }}\n          cp -r ${{ env.dist_path }} /tmp/\n          ls -la /tmp/${{ env.dist_path }}\n          git checkout -b ${{ env.dist_branch }}\n          find . -mindepth 1 -maxdepth 1 ! -name \'.git\' -exec rm -rf {} +\n          # 移动所有非隐藏文件和目录\n          mv /tmp/${{ env.dist_path }}/* .\n          # 移动所有隐藏文件和目录，但排除 . 和 ..\n          shopt -s dotglob nullglob\n          mv /tmp/${{ env.dist_path }}/.[!.]* .\n          shopt -u dotglob nullglob\n          # shopt -s dotglob 命令将开启shell选项，使得通配符 * 包含以点开头的文件和目录。\n          # nullglob 选项确保当没有匹配的文件时，通配符不展开为自身。\n          # 之后，我们通过 shopt -u dotglob nullglob 命令关闭这些选项，以恢复默认行为。\n          ls -la\n          git add .\n          git commit -m \"[bot][skip actions] Merge changes into ${{ env.dist_branch }}\" || echo \"No changes to commit\"\n\n      - name: Push changes\n        id: push_changes\n        uses: ad-m/github-push-action@master\n        with:\n          branch: ${{ env.dist_branch }}\n          force: true\n          # force_with_lease: true # 仅在远程分支自上次检出以来没有其他提交的情况下成功\n        env:\n          github_token: ${{ secrets.MY_GH_TOKEN_W }} # 使用具有完全写入权限的 TOKEN（必须包含 workflows ）</code></pre><p>环境变量</p><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"yaml\"># ref https://docs.github.com/zh/actions/learn-github-actions/variables\nenv:\n  dist_path: \".github\"\n  dist_branch: \"main_-_dist_-_test\" # 不能包含 / 等字符</code></pre><div>dist_path 是需要发布的文件夹路径。</div><div>dist_branch 是发布的分支名，<span style=\"color: var(--main-color)\"><span style=\"color: var(--main-color)\">不能包含 / 等字符。以 </span></span><span style=\"color: var(--main-color)\">main_-_dist_-_test 为例，main 是主分支的名字，dist 是 distribution（分发）的简写，test 是分发的名字，符合海文东规范。</span></div><h3><span style=\"color: var(--main-color)\">Step 1 检出仓库</span></h3><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"yaml\">- name: Checkout repository\n  uses: actions/checkout@v4\n  with:\n          fetch-depth: 0  # 确保可以获取所有历史记录，以便合并\n          token: ${{ secrets.MY_GH_TOKEN_W }} # 使用具有完全写入权限的 TOKEN（必须包含 workflows ）</code></pre><p>使用 <span style=\"color: var(--main-color)\">actions/checkout 检出仓库，需要注意的便是设置具有 workflows 权限的 TOKEN。如果发布的文件夹路径不涉及工作流文件，则使用默认的自动生成的 GITHUB_TOKEN 即可。</span></p><h3><span style=\"color: var(--main-color)\">Step 2 配置 Git</span></h3><div><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"yaml\">- name: Set up Git\n  run: |\n          git config --global user.name \'GitHub Actions\'\n          git config --global user.email \'actions@github.com\'</code></pre><p>使用机器人即可</p></div><h3>Step 3 合并修改并提交</h3><div><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"yaml\">- name: Merge changes from main into dist_branch\n  run: |\n          git checkout ${{ env.main_branch }}\n          cp -r ${{ env.dist_path }} /tmp/\n          ls -la /tmp/${{ env.dist_path }}\n          git checkout -b ${{ env.dist_branch }}\n          find . -mindepth 1 -maxdepth 1 ! -name \'.git\' -exec rm -rf {} +\n          # 移动所有非隐藏文件和目录\n          mv /tmp/${{ env.dist_path }}/* .\n          # 移动所有隐藏文件和目录，但排除 . 和 ..\n          shopt -s dotglob nullglob\n          mv /tmp/${{ env.dist_path }}/.[!.]* .\n          shopt -u dotglob nullglob\n          # shopt -s dotglob 命令将开启shell选项，使得通配符 * 包含以点开头的文件和目录。\n          # nullglob 选项确保当没有匹配的文件时，通配符不展开为自身。\n          # 之后，我们通过 shopt -u dotglob nullglob 命令关闭这些选项，以恢复默认行为。\n          ls -la\n          git add .\n          git commit -m \"[bot][skip actions] Merge changes into ${{ env.dist_branch }}\" || echo \"No changes to commit\"</code></pre></div><h3>Step 4 推送到远端</h3><div><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"yaml\">- name: Push changes\n  id: push_changes\n  uses: ad-m/github-push-action@master\n  with:\n          branch: ${{ env.dist_branch }}\n          force: true\n          # force_with_lease: true # 仅在远程分支自上次检出以来没有其他提交的情况下成功\n  env:\n          github_token: ${{ secrets.MY_GH_TOKEN_W }} # 使用具有完全写入权限的 TOKEN（必须包含 workflows ）</code></pre><p>这里使用和检出仓库同样的TOKEN。</p><p>也可以使用 PR action，这里不赘述。</p><hr /></div><h2>fork 端工作流</h2><p>先附上完整的 action yaml 代码</p><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"yaml\"># refusing to allow a GitHub App to create or update workflow .github/workflows/XXXX.yml without workflows permission\n# 遇到这个错误，需要检查三点：\n# 1. 仓库设置 Workflow permissions 允许写权限\n# 2. 使用的 TOKEN 具有 workflows 权限，如果是组织仓库还需要检查 TOKEN 的作用域\n# 3. actions/checkout 也需要使用 workflows 权限的 TOKEN\n# -------------------------------------------------------------------\n# https://github.com/Soltus\n\nname: \'Upstream Sync\'\n\non:\n  schedule:\n    #        ┌───────────── minute (0 - 59)\n    #        │  ┌───────────── hour (0 - 23)\n    #        │  │ ┌───────────── day of the month (1 - 31)\n    #        │  │ │ ┌───────────── month (1 - 12 or JAN-DEC)\n    #        │  │ │ │ ┌───────────── day of the week (0 - 6 or SUN-SAT)\n    #        │  │ │ │ │\n    #        │  │ │ │ │\n    #        │  │ │ │ │\n    #        *  * * * *\n    - cron: \'0 7 * * *\'\n    # scheduled at 07:00 every day\n\n  workflow_dispatch:  # click the button on Github repo!\n    inputs:\n      sync_test_mode: # Adds a boolean option that appears during manual workflow run for easy test mode config\n        description: \'Fork Sync Test Mode\'\n        type: boolean\n        default: false\n\nenv:\n    target_sync_branch: \"main\"\n    upstream_sync_repo: \"Soltus/Sillot-ASP\"\n    upstream_sync_branch: \"main_-_dist_-_test\"\n\njobs:\n  sync_latest_from_upstream:\n    runs-on: ubuntu-latest\n    name: Sync latest commits from upstream repo\n\n    steps:\n    # REQUIRED step\n    # Step 1: run a standard checkout action, provided by github\n    - name: Checkout target repo\n      uses: actions/checkout@v4\n      with:\n        # optional: set the branch to checkout,\n        # sync action checks out your \'target_sync_branch\' anyway\n        ref:  ${{ env.target_sync_branch }}\n        token: ${{ secrets.MY_GH_TOKEN_W }} # 使用具有完全写入权限的 TOKEN（必须包含 workflows ）\n        # REQUIRED if your upstream repo is private (see wiki)\n        persist-credentials: false\n\n    # REQUIRED step\n    # Step 2: run the sync action\n    - name: Sync upstream changes\n      id: sync\n      uses: aormsby/Fork-Sync-With-Upstream-action@v3.4.1\n      with:\n        target_sync_branch: ${{ env.target_sync_branch }}\n        target_repo_token: ${{ secrets.MY_GH_TOKEN_W }} # 可能涉及 workflows 的修改，需要对应权限\n        upstream_sync_branch: ${{ env.upstream_sync_branch }}\n        upstream_sync_repo: ${{ env.upstream_sync_repo }}\n        # Set test_mode true during manual dispatch to run tests instead of the true action!!\n        test_mode: ${{ inputs.sync_test_mode }}\n\n    # Step 3: Display a sample message based on the sync output var \'has_new_commits\'\n    - name: New commits found\n      if: steps.sync.outputs.has_new_commits == \'true\'\n      run: echo \"New commits were found to sync.\"\n\n    - name: No new commits\n      if: steps.sync.outputs.has_new_commits == \'false\'\n      run: echo \"There were no new commits.\"\n\n    - name: Show value of \'has_new_commits\'\n      run: echo ${{ steps.sync.outputs.has_new_commits }}</code></pre><p>fork 端工作流是为了应对前文提到的<span style=\"color: var(--main-color)\">小缺陷，即 fokr 端非同名分支后续目标会被强制改为上游默认分支的问题。需要注意的便是 fork 端工作流同样需要具有 workflows 权限的 TOKEN，原因见前文。</span></p><p>fork 仓库后，首先将发布的分支名（<span style=\"color: var(--main-color)\">main_-_dist_-_test）</span>设为主分支，然后删除原主分支（main），接着执行一次手动工作流触发，生成 <span style=\"color: var(--main-color)\">target_sync_branch （main）。</span></p><p>这里 <span style=\"color: var(--main-color)\">target_sync_branch （main）作为名义主分支，用于 release 等场景，而发布的分支名（main_-_dist_-_test）为实际主分支，用于同步上游更新的工作流文件。</span></p>\";s:10:\"post_title\";s:60:\"【独家首发】汐洛的Github分支发布工作流分享\";s:12:\"post_excerpt\";s:0:\"\";s:11:\"post_status\";s:7:\"publish\";s:14:\"comment_status\";s:4:\"open\";s:11:\"ping_status\";s:4:\"open\";s:13:\"post_password\";s:0:\"\";s:9:\"post_name\";s:168:\"%e3%80%90%e7%8b%ac%e5%ae%b6%e9%a6%96%e5%8f%91%e3%80%91%e6%b1%90%e6%b4%9b%e7%9a%84github%e5%88%86%e6%94%af%e5%8f%91%e5%b8%83%e5%b7%a5%e4%bd%9c%e6%b5%81%e5%88%86%e4%ba%ab\";s:7:\"to_ping\";s:0:\"\";s:6:\"pinged\";s:0:\"\";s:13:\"post_modified\";s:19:\"2024-07-20 05:46:18\";s:17:\"post_modified_gmt\";s:19:\"2024-07-19 21:46:18\";s:21:\"post_content_filtered\";s:0:\"\";s:11:\"post_parent\";i:0;s:4:\"guid\";s:22:\"https://sypai.cc/?p=52\";s:10:\"menu_order\";i:0;s:9:\"post_type\";s:4:\"post\";s:14:\"post_mime_type\";s:0:\"\";s:13:\"comment_count\";s:1:\"1\";s:6:\"filter\";s:3:\"raw\";}i:3;O:7:\"WP_Post\":24:{s:2:\"ID\";i:63;s:11:\"post_author\";s:1:\"4\";s:9:\"post_date\";s:19:\"2024-07-18 12:30:06\";s:13:\"post_date_gmt\";s:19:\"2024-07-18 04:30:06\";s:12:\"post_content\";s:941:\"<pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\">/* 块引用*/\ndiv.protyle-wysiwyg [data-node-id] span[data-type=\"block-ref\"]{\n    color:var(--b3-theme-primary) !important;\n    padding:0 5px;\n    border-radius: 6px ;\n    border-top:1px dashed var(--b3-theme-primary) !important;\n    border-left: 8px solid var(--b3-theme-primary) !important;\n    border-right: 8px  solid var(--b3-theme-primary) !important;\n    border-bottom:1px dashed var(--b3-theme-primary) !important;\n    border-image:unset !important;\n}\n\n/* 块引用 静态*/\ndiv.protyle-wysiwyg [data-node-id] span[data-type=\"block-ref\"][data-subtype=\"s\"]{\n    border-left: 13px solid var(--b3-theme-primary) !important;\n    border-right: 1px dashed !important;\n}</code></pre><p><img src=\"https://sypai.cc/wp-content/uploads/2024/07/image.png\" alt=\"image\" data-edit-file-id=\"60\" data-full-url=\"https://sypai.cc/wp-content/uploads/2024/07/image.png\" /></p><p> </p>\";s:10:\"post_title\";s:48:\"块引用样式 | 区分动态和静态锚文本\";s:12:\"post_excerpt\";s:0:\"\";s:11:\"post_status\";s:7:\"publish\";s:14:\"comment_status\";s:4:\"open\";s:11:\"ping_status\";s:4:\"open\";s:13:\"post_password\";s:0:\"\";s:9:\"post_name\";s:136:\"%e5%9d%97%e5%bc%95%e7%94%a8%e6%a0%b7%e5%bc%8f-%e5%8c%ba%e5%88%86%e5%8a%a8%e6%80%81%e5%92%8c%e9%9d%99%e6%80%81%e9%94%9a%e6%96%87%e6%9c%ac\";s:7:\"to_ping\";s:0:\"\";s:6:\"pinged\";s:0:\"\";s:13:\"post_modified\";s:19:\"2024-07-18 12:30:06\";s:17:\"post_modified_gmt\";s:19:\"2024-07-18 04:30:06\";s:21:\"post_content_filtered\";s:0:\"\";s:11:\"post_parent\";i:0;s:4:\"guid\";s:163:\"https://sypai.cc/archives/%e5%9d%97%e5%bc%95%e7%94%a8%e6%a0%b7%e5%bc%8f-%e5%8c%ba%e5%88%86%e5%8a%a8%e6%80%81%e5%92%8c%e9%9d%99%e6%80%81%e9%94%9a%e6%96%87%e6%9c%ac/\";s:10:\"menu_order\";i:0;s:9:\"post_type\";s:4:\"post\";s:14:\"post_mime_type\";s:0:\"\";s:13:\"comment_count\";s:1:\"0\";s:6:\"filter\";s:3:\"raw\";}i:4;O:7:\"WP_Post\":24:{s:2:\"ID\";i:76;s:11:\"post_author\";s:1:\"5\";s:9:\"post_date\";s:19:\"2024-07-18 13:36:56\";s:13:\"post_date_gmt\";s:19:\"2024-07-18 05:36:56\";s:12:\"post_content\";s:2955:\"<p id=\"20240718133137-f2w6p10\">这个包可以可以帮开发者在插件更新的时候展示更新日志。</p><ul id=\"20240718133236-bvoyuea\"><li id=\"20240718133237-bdx2s36\"><a href=\"https://github.com/frostime/sy-plugin-changelog\">https://github.com/frostime/sy-plugin-changelog</a></li><li id=\"20240718133238-qtd323q\"><a href=\"https://www.npmjs.com/package/sy-plugin-changelog\">https://www.npmjs.com/package/sy-plugin-changelog</a></li></ul><p id=\"20240718133236-5u7s6l4\">基本用法如下：</p><ol id=\"20240718133207-trnul8y\"><li id=\"20240718133207-jnq6c77\"><p id=\"20240718133207-tq4inio\">安装依赖</p><blockquote id=\"20240718133207-ja8kse6\"><p id=\"20240718133207-szr7xm0\">pnpm add -D sy-plugin-changelog</p></blockquote></li><li id=\"20240718133207-x800eaj\"><p id=\"20240718133207-q0a7n3h\">在自己的代码中导入, 并将 <span data-type=\"code\">this</span>​ 作为参数传入 <span data-type=\"code\">changelog</span>​ 当中</p><pre><code class=\"language-typescript\">import { changelog } from \"sy-plugin-changelog\";\n\nexport default class PluginSample extends Plugin {\n  async onload() {\n    //... everything done\n    changelog(this);\n  }\n}\n</code></pre></li><li id=\"20240718133207-hi8fser\"><p id=\"20240718133207-lh6qliy\">请在 i18n 目录下新建文件 <span data-type=\"code\">CHANGELOG-&lt;lang&gt;-&lt;version&gt;.md</span>​ 文件, 并保证被加入打包当中</p><ul id=\"20240718133207-qtabshs\"><li id=\"20240718133207-ml9kx8t\">例如: i18n/CHANGELOG-zh_CN-0.0.2.md</li><li id=\"20240718133207-piq8m1z\">​<span data-type=\"code\">&lt;lang&gt;</span>​ 应当和思源 <span data-type=\"code\">window.siyuan.config.lang</span>​ 一致, 例如: zh_CN</li><li id=\"20240718133207-x8khhpf\">​<span data-type=\"code\">&lt;version&gt;</span>​ 应当和 plugin.json 中 version 字段一致, 且格式为 <span data-type=\"code\">/\\d+\\.\\d+\\.\\d+/</span>​, 如 <span data-type=\"code\">1.1.2</span>​</li></ul></li></ol><p id=\"20240718133225-9uat6zd\">更多详细的用法, 请阅读文档。</p><pre><code class=\"language-js\">/**\n *\n * @param plugin\n *      The plugin whose changelog you want to show\n * @param changelogPath\n *      Optional, the path of changelog file, default is `i18n/CHANGELOG-${lang}-${version}.md`\\\n *      You can specify a concrete path like `i18n/CHANGELOG-zh_CN-1.0.0.md`\\\n *      Or you can use placeholder `${lang}` and `${ver}`, `${version}, the ${lang} will be replaced with current language, and ${ver} will be replaced with the main version of the plugin.\n *\n * @param langFallback\n *      Optional, the fallback language\n *      default is:\\\n *      ‍```\n *      {\"zh_CN\": \"zh_CN\", \"zh_CHT\": \"zh_CN\", \"en_US\": \"en_US\", \"es_ES\": \"en_US\", \"fr_FR\": \"en_US\"}\n *      ‍```\n *      A custom langFallback will be merged with the default one.\n * @returns\n */\nexport declare function changelog(plugin: Plugin, changelogPath?: string, langFallback?: LangFallback): Promise&lt;ChangelogResult&gt;;\n</code></pre>\";s:10:\"post_title\";s:38:\"为你的插件添加 ChangeLog 功能\";s:12:\"post_excerpt\";s:0:\"\";s:11:\"post_status\";s:7:\"publish\";s:14:\"comment_status\";s:4:\"open\";s:11:\"ping_status\";s:4:\"open\";s:13:\"post_password\";s:0:\"\";s:9:\"post_name\";s:92:\"%e4%b8%ba%e4%bd%a0%e7%9a%84%e6%8f%92%e4%bb%b6%e6%b7%bb%e5%8a%a0-changelog-%e5%8a%9f%e8%83%bd\";s:7:\"to_ping\";s:0:\"\";s:6:\"pinged\";s:0:\"\";s:13:\"post_modified\";s:19:\"2024-07-18 13:36:59\";s:17:\"post_modified_gmt\";s:19:\"2024-07-18 05:36:59\";s:21:\"post_content_filtered\";s:0:\"\";s:11:\"post_parent\";i:0;s:4:\"guid\";s:22:\"https://sypai.cc/?p=76\";s:10:\"menu_order\";i:0;s:9:\"post_type\";s:4:\"post\";s:14:\"post_mime_type\";s:0:\"\";s:13:\"comment_count\";s:1:\"0\";s:6:\"filter\";s:3:\"raw\";}i:5;O:7:\"WP_Post\":24:{s:2:\"ID\";i:104;s:11:\"post_author\";s:1:\"1\";s:9:\"post_date\";s:19:\"2024-07-18 17:46:44\";s:13:\"post_date_gmt\";s:19:\"2024-07-18 09:46:44\";s:12:\"post_content\";s:14288:\"<p>出门在外面不方便笔记？思源笔记和微信重度使用者？免费微信笔记收集箱？敏感消息不想经过别人的手？有很多不同的消息想收集？想完全自定义自己的微信工具箱？有服务器闲置没事干？</p><p>好的，那么你来对地方了！</p><p>本项目可以为你制作一个完全的属于自己的微信收集箱。</p><p>项目开源地址：<a href=\"https://ld246.com/forward?goto=https%3A%2F%2Fgithub.com%2Fmuhanstudio%2Fsiyuan-wxbox\" target=\"_blank\" rel=\"nofollow noopener ugc\">https://github.com/muhanstudio/siyuan-wxbox</a></p><h2 id=\"演示图-\">演示图：</h2><p>文字：</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/Screenshot_2023-10-19-00-43-36-692_com.tencent.mm-20231019004425-544y7q5.jpg?imageView2/2/interlace/1/format/webp\" alt=\"Screenshot_2023-10-19-00-43-36-692_com.tencent.mm\" /></span></p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019004538-dswegt7.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>图片：</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/Screenshot_2023-10-19-00-46-24-498_com.tencent.mm-20231019004644-dd5d11u.jpg?imageView2/2/interlace/1/format/webp\" alt=\"Screenshot_2023-10-19-00-46-24-498_com.tencent.mm\" /></span></p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019004743-758i233.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>语音（自动识别成文字）：</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/Screenshot_2023-10-19-00-50-29-588_com.tencent.mm-20231019005058-m6xp5tf.jpg?imageView2/2/interlace/1/format/webp\" alt=\"Screenshot_2023-10-19-00-50-29-588_com.tencent.mm\" /></span></p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019005051-lux42c4.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>位置信息：</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/image-DDlhDdt.png?imageView2/2/interlace/1/format/webp\" alt=\"image.png\" /></span></p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/image-iE5rFVR.png?imageView2/2/interlace/1/format/webp\" alt=\"image.png\" /></span></p><h3 id=\"那么这么方便的微信记笔记怎么才能实现呢-\">那么这么方便的微信记笔记怎么才能实现呢？</h3><h2 id=\"拿起发财的小手-开干-\">拿起发财的小手，开干！</h2><h2 id=\"详细搭建教程\">详细搭建教程</h2><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018235631-ee8lfsv.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>点击后会弹出文件管理器，data 后面紧跟着的就是你的笔记本 ID，如图，笔记本 ID 就是：20231018093815-glkj4gy</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018235759-f465r17.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>然后就可以开始我们的搭建了</p><h2 id=\"搭建步骤-\">搭建步骤：</h2><h3 id=\"1-安装宝塔\">1.安装宝塔</h3><p>（异常简单，省略，可以参考百度）</p><h3 id=\"2-安装docker-并用docker部署思源笔记\">2.安装 docker，并用 docker 部署思源笔记</h3><p>（具体教程可以参考<a href=\"https://ld246.com/article/1635558111871\">思源笔记 docker 搭建及后续使用优化（小白向） - 链滴 (ld246.com)</a>）</p><p>你也可以直接按照我的 docker 来配置，只需要安装最新版宝塔，然后点击</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018223602-3q24msz.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>点击后，如果你没有安装 docker，界面发白会提示让你安装 docker-compose up，安装即可。</p><p>安装完成后，把本项目的 docker-compose.yml 放到文件 →/www/SiYuan/下面</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018224057-06ptu8e.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>修改其中的访问密码 123456 为你自己的密码</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019001109-fl66dv9.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>然后进入终端：</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018224729-dwo5a4f.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>在终端运行以下命令：</p><div class=\"vditor-copy\"><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\">cd /www/SiYuan\ndocker-compose up</code></pre></div><p>等待安装完成，思源笔记已经成功部署在 docker 中，工作空间在服务器的/www/SiYuan 中，你可以随时查看笔记的数据。</p><p>后续想要更新思源，进入终端运行以下命令即可更新思源：</p><div class=\"vditor-copy\"><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\">cd /www/SiYuan\ndocker-compose stop\ndocker-compose rm siyuan\ndocker rmi b3log/siyuan\ndocker-compose up</code></pre></div><h3 id=\"3-配置反向代理\">3.配置反向代理</h3><p>宝塔新建网站</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018225027-q7rke04.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>点击设置</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018225128-wx62nb7.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>点击反向代理，点击添加反向代理</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019000839-biqtca0.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>创建如下两个反向代理：</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018225245-zmnx6a6.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018225356-b62d9cu.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><h3 id=\"4-获取思源笔记docker客户端的参数-以下操作均在思源笔记docker客户端中进行-不是本地思源-\">4.获取思源笔记 docker 客户端的参数(以下操作均在思源笔记 docker 客户端中进行，不是本地思源)</h3><p>访问 http://你的域名/siyuan，就可以进入到思源笔记 docker 客户端，然后输入密码，进入客户端</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019001530-t15s4eh.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>登录你的账号，把自动同步打开，如果是订阅账号就别管其他的，查看 apitoken 就可以了，如果是非订阅的 S3 储存用户，则还需要配置你的云同步参数，然后打开自动云同步</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019001911-8kboezy.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>然后去设置里面看自己的 apitoken，保存，一会儿要用</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019002035-gs9yh7b.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>之后一定要手动点击一次同步，一定一定要同步完成一次。</p><h3 id=\"5-获取微信公众号参数\">5.获取微信公众号参数</h3><p>登录微信公众平台，获取这些参数：token,appid,aeskey</p><p>appid 在这里查看</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018231006-qnkgcl7.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>然后往下翻，有一个服务器配置，没有启用的话点击启用，启用了就点击修改</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018231220-1zkuu7l.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>在这里填写你的收集箱机器人地址，并获取剩余的参数，token 随机填一串数字，建议复杂一点，AESKEY 随机生成，不要点击提交，保留这个页面，如图：</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/image-NdLuppj.png?imageView2/2/interlace/1/format/webp\" alt=\"image.png\" /></span></p><h3 id=\"6-上传项目\">6.上传项目</h3><p>将本项目的 config.py，robot.py，run.py 拷贝到服务器的某个目录下面，我这里的路径是/www/wwwroot/app/wxsh</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018232232-su1pt41.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><h3 id=\"7-修改参数\">7.修改参数</h3><p>修改 config.py 为你刚刚得到微信公众号中的参数</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018233614-sth69wn.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>修改 robot.py 中的参数为你的思源笔记参数，其中 openid 先不用管，后面我们会回来改</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019000050-x370hif.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><h3 id=\"8-运行项目\">8.运行项目</h3><p>进入软件商店，应用搜索 python，安装 python 项目管理器</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018232409-wgihmoh.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>安装完成后点击设置</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018232537-6iiy2r5.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>点击版本管理，安装一个你喜欢的版本，建议和我一样</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018232627-46euykg.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>然后点击项目管理，选择项目路径，找到你放 py 文件的地方，然后选择</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018233009-8fdp1ne.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>点击启动文件/文件夹，选择 run.py</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018233116-p9z2wjq.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>最后应该如下图所示，然后点击确定就可以，我们的微信公众号监控就开始运行了，</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231018233222-wsdcsad.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>点击然后不管报不报错，都点击模块</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019010252-6lrzabp.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>需要安装两个模块，flask 和 werobot，输入模块名称然后点击添加就可以了，两个都要添加</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019010325-rzm46i5.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>添加完成后，点击重启即可</p><p>此时，这个界面就可以点击提交了</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/image-M8AfaFE.png?imageView2/2/interlace/1/format/webp\" alt=\"image.png\" /></span></p><h3 id=\"9-获取openid-修改参数-重启项目\">9.获取 openid，修改参数，重启项目</h3><p>然后我们就可以去给公众号发消息了，第一次发消息，会回复你：此用户无权限：xxx，其中 xxx 就是你的 openid，复制下来，打开宝塔面板，找到你放 robot.py 的地方，修改里面的 openid 为你的 openid</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019002611-0twkbb6.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>修改完成保存后，再次进入 python 项目管理器，项目管理，点击重启</p><p><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231019002716-7chwjgk.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>等待重启完成，你就可以自由自在给自己的公众号发语音，图片，文字来达到随时随地记笔记的效果啦！</p><h2 id=\"真棒-你居然做完了-你真的很努力也很认真-加油---\">真棒，你居然做完了，你真的很努力也很认真，加油！！！</h2>\";s:10:\"post_title\";s:57:\"(硬核向) 自制思源笔记微信收集箱搭建教程\";s:12:\"post_excerpt\";s:0:\"\";s:11:\"post_status\";s:7:\"publish\";s:14:\"comment_status\";s:4:\"open\";s:11:\"ping_status\";s:4:\"open\";s:13:\"post_password\";s:0:\"\";s:9:\"post_name\";s:163:\"%e7%a1%ac%e6%a0%b8%e5%90%91-%e8%87%aa%e5%88%b6%e6%80%9d%e6%ba%90%e7%ac%94%e8%ae%b0%e5%be%ae%e4%bf%a1%e6%94%b6%e9%9b%86%e7%ae%b1%e6%90%ad%e5%bb%ba%e6%95%99%e7%a8%8b\";s:7:\"to_ping\";s:0:\"\";s:6:\"pinged\";s:0:\"\";s:13:\"post_modified\";s:19:\"2024-07-18 23:46:59\";s:17:\"post_modified_gmt\";s:19:\"2024-07-18 15:46:59\";s:21:\"post_content_filtered\";s:0:\"\";s:11:\"post_parent\";i:0;s:4:\"guid\";s:190:\"https://sypai.cc/archives/%e7%a1%ac%e6%a0%b8%e5%90%91-%e8%87%aa%e5%88%b6%e6%80%9d%e6%ba%90%e7%ac%94%e8%ae%b0%e5%be%ae%e4%bf%a1%e6%94%b6%e9%9b%86%e7%ae%b1%e6%90%ad%e5%bb%ba%e6%95%99%e7%a8%8b/\";s:10:\"menu_order\";i:0;s:9:\"post_type\";s:4:\"post\";s:14:\"post_mime_type\";s:0:\"\";s:13:\"comment_count\";s:1:\"0\";s:6:\"filter\";s:3:\"raw\";}i:6;O:7:\"WP_Post\":24:{s:2:\"ID\";i:107;s:11:\"post_author\";s:1:\"5\";s:9:\"post_date\";s:19:\"2024-07-18 18:03:37\";s:13:\"post_date_gmt\";s:19:\"2024-07-18 10:03:37\";s:12:\"post_content\";s:853:\"<pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"css\">.protyle-wysiwyg [data-node-id] span[data-type~=\"block-ref\"]::before {\n    content: \"[\";\n    color: var(--b3-theme-primary);\n    display: inline-block;\n    text-align: center;\n    margin: 0 0 0 0.125em;\n    padding: 0;\n    position: inherit;\n    font-size: 1em;\n}\n\n.protyle-wysiwyg [data-node-id] span[data-type~=\"block-ref\"]::after {\n    content: \"]\";\n    color: var(--b3-theme-primary);\n    display: inline-block;\n    text-align: center;\n    margin: 0 0.125em 0 0;\n    padding: 0;\n    position: inherit;\n    font-size: 1em;\n}</code></pre><p>效果如下：</p><p><img src=\"https://sypai.cc/wp-content/uploads/2024/07/image-2.png\" alt=\"image\" data-edit-file-id=\"106\" data-full-url=\"https://sypai.cc/wp-content/uploads/2024/07/image-2.png\" /></p><p> </p>\";s:10:\"post_title\";s:21:\"双链被 `[]` 包裹\";s:12:\"post_excerpt\";s:0:\"\";s:11:\"post_status\";s:7:\"publish\";s:14:\"comment_status\";s:4:\"open\";s:11:\"ping_status\";s:4:\"open\";s:13:\"post_password\";s:0:\"\";s:9:\"post_name\";s:46:\"%e5%8f%8c%e9%93%be%e8%a2%ab-%e5%8c%85%e8%a3%b9\";s:7:\"to_ping\";s:0:\"\";s:6:\"pinged\";s:0:\"\";s:13:\"post_modified\";s:19:\"2024-07-18 18:03:37\";s:17:\"post_modified_gmt\";s:19:\"2024-07-18 10:03:37\";s:21:\"post_content_filtered\";s:0:\"\";s:11:\"post_parent\";i:0;s:4:\"guid\";s:73:\"https://sypai.cc/archives/%e5%8f%8c%e9%93%be%e8%a2%ab-%e5%8c%85%e8%a3%b9/\";s:10:\"menu_order\";i:0;s:9:\"post_type\";s:4:\"post\";s:14:\"post_mime_type\";s:0:\"\";s:13:\"comment_count\";s:1:\"0\";s:6:\"filter\";s:3:\"raw\";}i:7;O:7:\"WP_Post\":24:{s:2:\"ID\";i:111;s:11:\"post_author\";s:1:\"5\";s:9:\"post_date\";s:19:\"2024-07-18 18:44:49\";s:13:\"post_date_gmt\";s:19:\"2024-07-18 10:44:49\";s:12:\"post_content\";s:420:\"<p>思源的默认字体不知道为什么总是感觉有点发虚。</p><p>所以我一般习惯通过 CSS 把 body 字重调的稍微大一点（500）；至于编辑区，就还是保持原始的不动即可。</p><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"css\">body {\n  font-size: 16px;\n  font-weight: 500;\n}\n\ndiv.protyle-wysiwyg {\n  font-weight: 400;\n}</code></pre>\";s:10:\"post_title\";s:33:\"建议更改思源的默认字重\";s:12:\"post_excerpt\";s:0:\"\";s:11:\"post_status\";s:7:\"publish\";s:14:\"comment_status\";s:4:\"open\";s:11:\"ping_status\";s:4:\"open\";s:13:\"post_password\";s:0:\"\";s:9:\"post_name\";s:99:\"%e5%bb%ba%e8%ae%ae%e6%9b%b4%e6%94%b9%e6%80%9d%e6%ba%90%e7%9a%84%e9%bb%98%e8%ae%a4%e5%ad%97%e9%87%8d\";s:7:\"to_ping\";s:0:\"\";s:6:\"pinged\";s:0:\"\";s:13:\"post_modified\";s:19:\"2024-07-18 18:44:49\";s:17:\"post_modified_gmt\";s:19:\"2024-07-18 10:44:49\";s:21:\"post_content_filtered\";s:0:\"\";s:11:\"post_parent\";i:0;s:4:\"guid\";s:126:\"https://sypai.cc/archives/%e5%bb%ba%e8%ae%ae%e6%9b%b4%e6%94%b9%e6%80%9d%e6%ba%90%e7%9a%84%e9%bb%98%e8%ae%a4%e5%ad%97%e9%87%8d/\";s:10:\"menu_order\";i:0;s:9:\"post_type\";s:4:\"post\";s:14:\"post_mime_type\";s:0:\"\";s:13:\"comment_count\";s:1:\"0\";s:6:\"filter\";s:3:\"raw\";}i:8;O:7:\"WP_Post\":24:{s:2:\"ID\";i:130;s:11:\"post_author\";s:1:\"1\";s:9:\"post_date\";s:19:\"2024-07-18 20:42:46\";s:13:\"post_date_gmt\";s:19:\"2024-07-18 12:42:46\";s:12:\"post_content\";s:17007:\"<blockquote><p>插件开发上手太难？不如先来开发一个挂件练练手！</p></blockquote><h2 id=\"挂件是什么\">挂件是什么</h2><p>挂件是以可调整大小的 html 网页块儿形式展示在笔记正文里面的元素，可以通过快捷指令快速插入到笔记中，实现一些交互，记录，同时可以引入第三方 js，以及调用本地思源的 API 来实现更多功能，例如实现与思源笔记数据的交互。</p><h2 id=\"挂件文件内容\">挂件文件内容</h2><h3 id=\"语法-HTML\">语法：HTML</h3><p>HTML（HyperText Markup Language）是一种用于创建网页的标记语言。它由一系列的标签（tag）组成，这些标签用于描述网页的结构和内容。HTML 标记语言使用尖括号（&lt;&gt;）将标签包围起来，标签通常成对出现，分为开始标签和结束标签。</p><p>HTML 通过使用不同的标签来定义网页的结构，包括标题、段落、链接、图像、表格等元素。标签可以包含属性，用于提供关于元素的额外信息，如颜色、大小、链接目标等。</p><p>HTML 是构建网页的基础，它与 CSS（层叠样式表）和 JavaScript 等技术一起使用，可以实现丰富的网页交互和样式效果。通过编写 HTML 代码，开发人员可以创建具有文本、图像、链接和多媒体等内容的网页，并将其发布到互联网上供用户访问。<br />当创建网页时，使用 HTML 可以定义页面的结构和内容。下面是一些常用的 HTML 标签和它们的作用：</p><ol><li><code>&lt;html&gt;</code>：定义 HTML 文档的根元素。</li><li><code>&lt;head&gt;</code>：定义文档的头部，包含了一些元数据和链接引用。</li><li><code>&lt;title&gt;</code>：定义网页的标题，显示在浏览器的标题栏或标签页上。</li><li><code>&lt;body&gt;</code>：定义文档的主体部分，包含了网页的实际内容。</li><li><code>&lt;h1&gt;</code> 到 <code>&lt;h6&gt;</code>：定义标题，从大到小依次递减，用于显示不同级别的标题。</li><li><code>&lt;p&gt;</code>：定义段落，用于显示一段文本。</li><li><code>&lt;a&gt;</code>：定义超链接，用于创建指向其他页面或资源的链接。</li><li><code>&lt;img&gt;</code>：定义图像，用于显示图片。</li><li><code>&lt;ul&gt;</code> 和 <code>&lt;li&gt;</code>：定义无序列表，用于显示项目列表。</li><li><code>&lt;ol&gt;</code> 和 <code>&lt;li&gt;</code>：定义有序列表，用于显示按顺序排列的项目列表。</li><li><code>&lt;table&gt;</code>、<code>&lt;tr&gt;</code> 和 <code>&lt;td&gt;</code>：定义表格，用于显示数据表格。</li><li><code>&lt;div&gt;</code>：定义文档中的一个区块，用于组织和样式化内容。</li><li><code>&lt;span&gt;</code>：定义文档中的一个内联元素，用于样式化文本的一部分。</li></ol><p>以下是一个简单的 HTML 示例，展示了如何使用上述标签来创建一个网页：</p><div class=\"vditor-copy\"><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"xml\">&lt;!DOCTYPE html&gt;\n&lt;html&gt;\n&lt;head&gt;\n  &lt;title&gt;我的网页&lt;/title&gt;\n&lt;/head&gt;\n&lt;body&gt;\n  &lt;h1&gt;欢迎来到我的网页&lt;/h1&gt;\n  &lt;p&gt;这是一个示例网页，用于演示HTML标记语言的基本用法。&lt;/p&gt;\n  \n  &lt;h2&gt;链接示例&lt;/h2&gt;\n  &lt;a href=\"https://www.example.com\"&gt;点击这里&lt;/a&gt;访问示例网站。\n  \n  &lt;h2&gt;图像示例&lt;/h2&gt;\n  &lt;img src=\"image.jpg\" alt=\"示例图片\"&gt;\n  \n  &lt;h2&gt;列表示例&lt;/h2&gt;\n  &lt;ul&gt;\n    &lt;li&gt;项目1&lt;/li&gt;\n    &lt;li&gt;项目2&lt;/li&gt;\n    &lt;li&gt;项目3&lt;/li&gt;\n  &lt;/ul&gt;\n  \n  &lt;h2&gt;表格示例&lt;/h2&gt;\n  &lt;table&gt;\n    &lt;tr&gt;\n      &lt;td&gt;姓名&lt;/td&gt;\n      &lt;td&gt;年龄&lt;/td&gt;\n    &lt;/tr&gt;\n    &lt;tr&gt;\n      &lt;td&gt;张三&lt;/td&gt;\n      &lt;td&gt;25&lt;/td&gt;\n    &lt;/tr&gt;\n    &lt;tr&gt;\n      &lt;td&gt;李四&lt;/td&gt;\n      &lt;td&gt;30&lt;/td&gt;\n    &lt;/tr&gt;\n  &lt;/table&gt;\n&lt;/body&gt;\n&lt;/html&gt;</code></pre></div><p>在上面的示例中，<code>&lt;!DOCTYPE html&gt;</code> 声明了文档类型为 HTML5，<code>&lt;html&gt;</code> 标签是根元素，包含了整个 HTML 文档。<code>&lt;head&gt;</code> 标签内部定义了文档的头部信息，包括标题等。<code>&lt;body&gt;</code> 标签内部是文档的主体部分，包含了网页的实际内容。</p><p>这只是一个简单的示例，HTML 标记语言非常丰富，还有更多的标签和属性可用于创建更复杂和交互性的网页。开发人员可以根据需要使用不同的标签和属性来实现所需的效果。</p><h3 id=\"核心文件\">核心文件</h3><h4 id=\"index-html\">index.html</h4><p>index.html 是挂件插入时会首先加载的界面，入口文件，通常作为主界面出现。</p><h4 id=\"widget-json\">widget.json</h4><p>挂件在集市里的信息，俗称商品描述，不能缺少，否则无法上架（如果你打算上架集市），<br />该文件内容的描述可见。</p><h2 id=\"开发步骤\">开发步骤</h2><h3 id=\"UI\">UI</h3><p>可以通过不同的框架进行开发，例如 vue 和其他的模板语言，但最后都要编译为 HTML。</p><h3 id=\"后端\">后端</h3><p>后端的交互通过 js 实现，引入 js 或者自行编写 js，思源允许在 html 内执行 js。</p><h3 id=\"数据的存储\">数据的存储</h3><p>目前挂件没有专属的很好的储存数据的 API，以下是一个储存数据的思路。</p><h4 id=\"获取数据唯一性标识\">获取数据唯一性标识</h4><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"javascript\">// 获取当前挂件的块id \nconst id = window.frameElement.parentElement.parentElement.getAttribute(\"data-node-id\");</code></pre><p>这里获取到的 <code>id</code> 是当前挂件所在的块 id，可以用来给数据加上唯一性标识，并和它所在的块儿联系起来。<br />示意图：<br /><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/12/siyuan/1687070401463/assets/Pasted_image_20231204153852-20231204170630-udanr7n.png?imageView2/2/interlace/1/format/webp\" alt=\"\" /></span></p><p>如图，成功让挂件在自己的界面输出了自己所在的块 id。</p><h4 id=\"储存数据\">储存数据</h4><p>获得了可以作为数据唯一键对值的 id，下一步就是想办法储存数据，我们可以借助官方给出的 API 文档中的写入文件的 API 来进行数据存储。<br /><a href=\"https://ld246.com/forward?goto=https%3A%2F%2Fgithub.com%2Fsiyuan-note%2Fsiyuan%2Fblob%2Fmaster%2FAPI_zh_CN.md%23%25E5%2586%2599%25E5%2585%25A5%25E6%2596%2587%25E4%25BB%25B6\" target=\"_blank\" rel=\"nofollow noopener ugc\">思源后台写入文件 API</a></p><h5 id=\"写入文件的API\">写入文件的 API</h5><ul><li><p><code>/api/file/putFile</code></p></li><li><p>参数为 HTTP Multipart 表单</p><ul><li><code>path</code>：工作空间路径下的文件路径</li><li><code>isDir</code>：是否为创建文件夹，为 <code>true</code> 时仅创建文件夹，忽略 <code>file</code></li><li><code>modTime</code>：最近访问和修改时间，Unix time</li><li><code>file</code>：上传的文件</li><li>返回值</li></ul><div class=\"vditor-copy\"><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"json\">{\n  \"code\": 0,\n  \"msg\": \"\",\n  \"data\": null\n}</code></pre></div></li></ul><h5 id=\"将数据写入文件的示例\">将数据写入文件的示例<code class=\"language-js highlight-chroma\"></code></h5><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"javascript\">// 写入文件内容\nfunction putFileContent(path, content) {\n	const formData = new FormData();\n    formData.append(\"path\", path);\n    formData.append(\"file\", new Blob([content]));\n	return fetch(\"/api/file/putFile\", {\n          method: \"POST\",\n          body: formData,\n    }).then((response) =&gt; {\n            if (response.ok) {\n              console.log(\"File saved successfully\");\n            } else {\n              throw new Error(\"Failed to save file\");\n            }\n          })\n          .catch((error) =&gt; {\n            console.error(error);\n          });\n      }</code></pre><p>这个函数接受两个参数，path 和 content，其中 path 就是数据文件的路径，而 content 则是数据的内容，每次调用这个函数，都会将 content 的内容通过 api 接口追加到指定数据文件里面。</p><h5 id=\"数据的储存格式\">数据的储存格式</h5><p>我个人比较推荐 json 的键对值储存方式，下面的这个示例也是用的 json 格式储存数据。</p><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\">function saveIdCityMap(id, city) {\n        var idCityMap = getIdCityMap();\n        idCityMap[id] = city;\n        // 将 idCityMap 写入文件\n        putFileContent(\n          \"/data/widgets/Simple-weather-forecast/data/data.json\",\n          JSON.stringify(idCityMap)\n        );\n      }</code></pre><p><code class=\"language-js highlight-chroma\"><br /></code>​</p><p><code>saveIdCityMap</code> 函数接受两个参数：<code>id</code> 和 <code>city</code>，的目的是将 <code>id</code> 和 <code>city</code> 的映射保存到一个文件中。 <code>getIdCityMap</code> 函数用来获取一个名为 <code>idCityMap</code> 的对象，这个对象包含了已经存在的 ID 和城市的映射，具体定义看下面的<a href=\"https://b3logfile.com/file/2023/12/siyuan/1687070401463/#执行数据读取后的操作\">实例</a>。将新的 <code>id</code> 和 <code>city</code> 映射添加到 <code>idCityMap</code> 对象中，将更新后的 <code>idCityMap</code> 对象转换成 JSON 格式的字符串，并将其写入名为 <code>data.json</code> 的文件中（假设 <code>putFileContent</code> 函数可以成功执行）。</p><h4 id=\"读取数据\">读取数据</h4><p>我们同样可以直接使用后端 API 来获取数据<br /><a href=\"https://ld246.com/forward?goto=https%3A%2F%2Fgithub.com%2Fsiyuan-note%2Fsiyuan%2Fblob%2Fmaster%2FAPI_zh_CN.md%23%25E8%258E%25B7%25E5%258F%2596%25E6%2596%2587%25E4%25BB%25B6\" target=\"_blank\" rel=\"nofollow noopener ugc\">后端获取文件 API</a></p><h5 id=\"获取文件\">获取文件</h5><ul><li><p><code>/api/file/getFile</code></p></li><li><p>参数</p></li></ul><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"json\">{\n  \"path\": \"/data/20210808180117-6v0mkxr/20200923234011-ieuun1p.sy\"\n}</code></pre><ul><li style=\"list-style-type: none;\"><ul><li><code>path</code>：工作空间路径下的文件路径</li></ul></li><li><p>返回值</p><ul><li>响应状态码 <code>200</code>: 文件内容</li><li>响应状态码 <code>202</code>: 异常信息</li></ul></li></ul><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\" data-enlighter-language=\"json\">{\n  \"code\": 404,\n  \"msg\": \"\",\n  \"data\": null\n}</code></pre><h5 id=\"获取数据函数实例\">获取数据函数实例</h5><p>通过这个 API 我们来构建读取数据的函数，同样是解析 json 的格式：</p><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\">// 获取文件内容\n      function getFileContent(path) {\n        return fetch(\"/api/file/getFile\", {\n          method: \"POST\",\n          headers: {\n            \"Content-Type\": \"application/json\",\n          },\n          body: JSON.stringify({\n            path: path,\n          }),\n        })\n          .then((response) =&gt; {\n            if (response.ok) {\n              return response.text();\n            } else {\n              throw new Error(\"Failed to get file content\");\n            }\n          })\n          .catch((error) =&gt; {\n            console.error(error);\n          });\n      }</code></pre><p><code class=\"language-js highlight-chroma\"><br /></code>​</p><p>这时候我们就可以在其他地方通过调用这个函数，通过判断返回结果，来判断执行我们需要的响应。</p><h5 id=\"执行数据读取后的操作\">执行数据读取后的操作</h5><pre class=\"enlighter-pre\" contenteditable=\"false\"><code class=\"gl\">getIdCityMap()\n        .then(function (idCityMap) {\n          var idCityMapJ = {};\n\n          // 使用正则表达式提取键值对中的键和值\n          var regex = /\"([^\"]+)\":\"([^\"]+)\"/g;\n          var match;\n          while ((match = regex.exec(idCityMap)) !== null) {\n            var key = match[1];\n            var value = match[2];\n            idCityMapJ[key] = value;\n          }\n\n          var city = idCityMapJ[id];\n          console.log(city); // 检查 city 变量的值是否正确获取到\n\n          if (city) {\n          //执行自定义函数\n            loadWeatherWidget(city);\n          } else {\n          //执行自定义函数\n            showInputBox();\n          }\n        })\n        .catch(function (error) {\n          console.log(\"获取 idCityMap 出错:\", error);\n        });</code></pre><p>这里通过寻找有没有 id 对应的 city，来返回结果，可以自定义在两种情况下进行什么自定义操作。</p><h3 id=\"与插件联通\">与插件联通</h3><p>一些插件提供了可供前台调用的插件 API，我们可以通过调用这些 API 来实现更多功能，例如<a href=\"https://ld246.com/forward?goto=https%3A%2F%2Fgithub.com%2Ffrostime%2Fsy-run-js\" target=\"_blank\" rel=\"nofollow noopener ugc\">运行 javascript</a> 插件就为我们开放了几乎所有的 api 端口：<br /><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/12/siyuan/1687070401463/assets/Pasted_image_20231204161953-20231204170630-1ipj0gm.png?imageView2/2/interlace/1/format/webp\" alt=\"\" /></span></p><p>当然，我们也可以在外部编写 js，通过该插件提供的接口，将代码片段传入，来运行我们的 js 脚本：<br />详情请见<a href=\"https://ld246.com/forward?goto=https%3A%2F%2Fgithub.com%2Ffrostime%2Fsy-run-js%2Fcommit%2Fd002c4a5e70b929cfd19e5113208cc447f5e77df\" target=\"_blank\" rel=\"nofollow noopener ugc\">添加 run-js-code 的 api</a><br /><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/12/siyuan/1687070401463/assets/Pasted_image_20231204162145-20231204170630-kmy8e5t.png?imageView2/2/interlace/1/format/webp\" alt=\"\" /></span></p><h2 id=\"发布到集市\">发布到集市</h2><h3 id=\"注册github并发布版本\">注册 github 并发布版本</h3><p>集市的挂件系统通过对 github 仓库的发布版本进行定时抓取来更新插件，实际抓取的是 package.zip 这个压缩包，这个压缩包里面应该至少有 index.html，widget.json，README.md（英文说明），README_zh_CN.md（中文说明）这四个文件，如果你的挂件还需要引用外部资源（js，png，svg 等等），可以用相对路径进行引用，然后一块儿压缩到 package.zip 里面，通过 release 来发布这个 package.zip，一定要用版本号作为 tag，否则不予索引，例如下图，版本号是 0.4.0<br /><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/12/siyuan/1687070401463/assets/Pasted_image_20231204162525-20231204170630-ksti278.png?imageView2/2/interlace/1/format/webp\" alt=\"|500\" /></span></p><h3 id=\"第一次发布\">第一次发布</h3><p>第一次发布首先要占坑，也就是署名索引，需要到集市仓库地址的<a href=\"https://ld246.com/forward?goto=https%3A%2F%2Fgithub.com%2Fsiyuan-note%2Fbazaar%2Fblob%2Fmain%2Fwidgets.json\" target=\"_blank\" rel=\"nofollow noopener ugc\">挂件索引</a>位置，点击编辑，在最后一行添加你的 <code>github用户名/挂件仓库名</code>，然后可能会提示你需要 fork 本仓库才能编辑，fork 即可，写完直接 commit，然后 creat pull request，等待开发者审核收录即可，可以在 <a href=\"https://ld246.com/forward?goto=https%3A%2F%2Fgithub.com%2Fsiyuan-note%2Fbazaar%2Fpulls\" target=\"_blank\" rel=\"nofollow noopener ugc\">pull request</a> 查看收录情况。<br /><strong>例如</strong><br />将末尾：<br /><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/12/siyuan/1687070401463/assets/Pasted_image_20231204162655-20231204170630-t6gy0gz.png?imageView2/2/interlace/1/format/webp\" alt=\"|500\" /></span></p><p>修改为：<br /><span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/12/siyuan/1687070401463/assets/Pasted_image_20231204163237-20231204170630-2y2ajmf.png?imageView2/2/interlace/1/format/webp\" alt=\"\" /></span></p><h3 id=\"后续版本更新\">后续版本更新</h3><p>后续修改完挂件代码后，只需要更改 widget 里面的 version 为更高的版本，然后打包成 package.zip，在 github 上发布 release，tag 和更新的 version 统一即可，集市会隔一段时间自动收录。</p>\";s:10:\"post_title\";s:36:\"五分钟学会开发自己的挂件\";s:12:\"post_excerpt\";s:0:\"\";s:11:\"post_status\";s:7:\"publish\";s:14:\"comment_status\";s:4:\"open\";s:11:\"ping_status\";s:4:\"open\";s:13:\"post_password\";s:0:\"\";s:9:\"post_name\";s:108:\"%e4%ba%94%e5%88%86%e9%92%9f%e5%ad%a6%e4%bc%9a%e5%bc%80%e5%8f%91%e8%87%aa%e5%b7%b1%e7%9a%84%e6%8c%82%e4%bb%b6\";s:7:\"to_ping\";s:0:\"\";s:6:\"pinged\";s:0:\"\";s:13:\"post_modified\";s:19:\"2024-07-18 23:37:57\";s:17:\"post_modified_gmt\";s:19:\"2024-07-18 15:37:57\";s:21:\"post_content_filtered\";s:0:\"\";s:11:\"post_parent\";i:0;s:4:\"guid\";s:135:\"https://sypai.cc/archives/%e4%ba%94%e5%88%86%e9%92%9f%e5%ad%a6%e4%bc%9a%e5%bc%80%e5%8f%91%e8%87%aa%e5%b7%b1%e7%9a%84%e6%8c%82%e4%bb%b6/\";s:10:\"menu_order\";i:0;s:9:\"post_type\";s:4:\"post\";s:14:\"post_mime_type\";s:0:\"\";s:13:\"comment_count\";s:1:\"0\";s:6:\"filter\";s:3:\"raw\";}i:9;O:7:\"WP_Post\":24:{s:2:\"ID\";i:132;s:11:\"post_author\";s:1:\"1\";s:9:\"post_date\";s:19:\"2024-07-18 20:59:02\";s:13:\"post_date_gmt\";s:19:\"2024-07-18 12:59:02\";s:12:\"post_content\";s:1858:\"<blockquote><p>这是一个思源笔记 js 应用示例库，里面有一些基本的 js 应用，你可以根据它们或组合它们来实现你需要的功能，它们也可以帮助你解读一些成品应用的代码片段</p></blockquote><p>开源地址：<a href=\"https://ld246.com/forward?goto=https%3A%2F%2Fgithub.com%2Fmuhanstudio%2Fsiyuan-jsexample\" target=\"_blank\" rel=\"nofollow noopener ugc\">muhanstudio/siyuan-jsexample: 思源笔记 js 应用示例库 (github.com)</a></p><p>国内访问：<a href=\"https://ld246.com/forward?goto=https%3A%2F%2Fdoc.muhan.studio%2F%23%2Fgithub%2Fsiyuan-jsexample%2F\" target=\"_blank\" rel=\"nofollow noopener ugc\">https://doc.muhan.studio/#/github/siyuan-jsexample</a></p><p>（PS：目前只是开库，写一些最基本的 js，思源的前端文档，限于本人个人水平，实在是看的有点麻，本来打算删了这个库不写了，但是想了想还是发布一下，说不定以后有时间有精力能写点功能复杂一点的）</p><p>​<span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231021193907-trv1ch2.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>快捷运行方法：</p><p>1.下载本笔记库，导入到你的笔记本中</p><p>2.在集市中安装以下插件</p><p>​<span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231021192418-23w1m8x.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>3.点击运行（如果有控制台输出，需要打开思源开发者工具）</p><p>​​<span class=\"vditor-reset__preview\"><img src=\"https://b3logfile.com/file/2023/10/siyuan/1687070401463/assets/image-20231021192728-2zrl31y.png?imageView2/2/interlace/1/format/webp\" alt=\"image\" /></span></p><p>​​</p>\";s:10:\"post_title\";s:31:\"思源 js 应用程序示例库\";s:12:\"post_excerpt\";s:0:\"\";s:11:\"post_status\";s:7:\"publish\";s:14:\"comment_status\";s:4:\"open\";s:11:\"ping_status\";s:4:\"open\";s:13:\"post_password\";s:0:\"\";s:9:\"post_name\";s:85:\"%e6%80%9d%e6%ba%90-js-%e5%ba%94%e7%94%a8%e7%a8%8b%e5%ba%8f%e7%a4%ba%e4%be%8b%e5%ba%93\";s:7:\"to_ping\";s:0:\"\";s:6:\"pinged\";s:0:\"\";s:13:\"post_modified\";s:19:\"2024-07-18 20:59:02\";s:17:\"post_modified_gmt\";s:19:\"2024-07-18 12:59:02\";s:21:\"post_content_filtered\";s:0:\"\";s:11:\"post_parent\";i:0;s:4:\"guid\";s:112:\"https://sypai.cc/archives/%e6%80%9d%e6%ba%90-js-%e5%ba%94%e7%94%a8%e7%a8%8b%e5%ba%8f%e7%a4%ba%e4%be%8b%e5%ba%93/\";s:10:\"menu_order\";i:0;s:9:\"post_type\";s:4:\"post\";s:14:\"post_mime_type\";s:0:\"\";s:13:\"comment_count\";s:1:\"0\";s:6:\"filter\";s:3:\"raw\";}}s:10:\"post_count\";i:10;s:12:\"current_post\";i:-1;s:11:\"before_loop\";b:1;s:11:\"in_the_loop\";b:0;s:4:\"post\";r:98;s:13:\"comment_count\";i:0;s:15:\"current_comment\";i:-1;s:11:\"found_posts\";i:121;s:13:\"max_num_pages\";i:13;s:21:\"max_num_comment_pages\";i:0;s:9:\"is_single\";b:0;s:10:\"is_preview\";b:0;s:7:\"is_page\";b:0;s:10:\"is_archive\";b:0;s:7:\"is_date\";b:0;s:7:\"is_year\";b:0;s:8:\"is_month\";b:0;s:6:\"is_day\";b:0;s:7:\"is_time\";b:0;s:9:\"is_author\";b:0;s:11:\"is_category\";b:0;s:6:\"is_tag\";b:0;s:6:\"is_tax\";b:0;s:9:\"is_search\";b:0;s:7:\"is_feed\";b:0;s:15:\"is_comment_feed\";b:0;s:12:\"is_trackback\";b:0;s:7:\"is_home\";b:0;s:17:\"is_privacy_policy\";b:0;s:6:\"is_404\";b:0;s:8:\"is_embed\";b:0;s:8:\"is_paged\";b:0;s:8:\"is_admin\";b:1;s:13:\"is_attachment\";b:0;s:11:\"is_singular\";b:0;s:9:\"is_robots\";b:0;s:10:\"is_favicon\";b:0;s:13:\"is_posts_page\";b:0;s:20:\"is_post_type_archive\";b:0;s:25:\"\0WP_Query\0query_vars_hash\";s:32:\"c82e1a097ca23bb8fe6ecedff22